  PRIMARY KEY (`id`),
  KEY `index_post_id` (`post_id`),
  KEY `index_user_id` (`user_id`),
  KEY `index_status` (`status`)
) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8;

