-- ----------------------------
-- Table structure for blog_post
-- ----------------------------
DROP TABLE IF EXISTS `blog_post`;
CREATE TABLE `blog_post` (
  `id` int(20) NOT NULL AUTO_INCREMENT,
  `title` varchar(128) DEFAULT NULL,
  `content` mediumtext,
  `excerpt` varchar(350) DEFAULT NULL COMMENT '摘录',
  `category_id` int(20) DEFAULT NULL,
  `status` int(11) DEFAULT NULL COMMENT '状态:1已删除;2已归档，已归档的内容禁止评论，文章不可删除;3草稿; 4已发布;5精华-->精华文章不可删除            6、已推至首页\\n',
  `read_count` int(11) NOT NULL DEFAULT '0' COMMENT '阅读次数',
  `comment_count` int(11) NOT NULL DEFAULT '0' COMMENT '评论次数',
  `enable_comment` int(11) NOT NULL DEFAULT '1' COMMENT '是否可评论 0不可以;1可以',
  `user_id` int(20) DEFAULT NULL,
  `user_name` varchar(64) DEFAULT NULL,
  `nick_name` varchar(64) DEFAULT NULL,
  `create_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `last_modify_time` timestamp NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP COMMENT '修改时间',
  PRIMARY KEY (`id`),
  KEY `index_user_id` (`user_id`) USING BTREE,
  KEY `index_category_id` (`category_id`)
) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8;

