-- Records of blog_post
-- ----------------------------
INSERT INTO `blog_post` VALUES ('1', '开篇初衷', '<p><span style=\"font-family: 楷体_GB2312; font-size: 14px;\">&nbsp; &nbsp; &nbsp; &nbsp;小编，就职于一家保险公司，基本上就是忙于投资清算、估值核算方面的日常业务操作和系统运维。</span></p><p style=\"text-indent: 37px;\"><span style=\"font-family: 楷体_GB2312; font-size: 14px;\">为啥想弄个网站出来呢？说是无聊也不过分，主要是系统管理员离职了，兼任其岗位近一年，有点感受越来越深：“公司内部很多项目实施过了，运行上线了，但接手后发现除了系统在正常运行之外，其他什么都没留下”。</span></p><p style=\"text-indent: 37px;\"><span style=\"font-family: 楷体_GB2312; font-size: 14px;\">似乎这个现象在保险信息系统行业还挺普遍。</span></p><p style=\"text-indent: 37px;\"><span style=\"font-family: 楷体_GB2312; font-size: 14px;\">抛开保险资产管理公司不谈，保险公司处于内部投资管理中心这个阶段的时候，信息系统支持力量相对非常薄弱，还容易扯皮。</span></p><p style=\"text-indent: 37px;\"><span style=\"font-family: 楷体_GB2312; font-size: 14px;\">投资业务系统最初也是公司信息部管理的，随着投资业务增多，由于其特殊性一般会在后期交由投资管理中心自己负责系统的运维。 </span></p><p style=\"text-indent: 37px;\"><span style=\"font-family: 楷体_GB2312; font-size: 14px;\">公司信息部主要负责传统保险理赔等核心系统，至于投资业务系统，一句我们不懂投资业务草草了事。</span></p><p style=\"text-indent: 37px;\"><span style=\"font-family: 楷体_GB2312; font-size: 14px;\">对于投资管理中心而言，也就是招聘个系统管理员负责业务系统的正常运行，其他基础环境还得指望总公司信息技术部。</span></p><p style=\"text-indent: 37px;\"><span style=\"font-family: 楷体_GB2312; font-size: 14px;\">职责看似分明，实际过程中免不了各种扯皮。权，公司信息技术部管着。事，投资管理中心系统管理员自己干。问题自然也就是各种不顺与扯皮，话说，作为系统管理员在投资管理中心和公司信息部的这个夹缝中生存，相当不容易啊。</span></p><p style=\"text-indent: 37px;\"><span style=\"font-family: 楷体_GB2312; font-size: 14px;\">回归主题， 近期公司又打算上个新业务，又要购买A、B、C系统的增值模块了，系统的开发商真爽---有新业务，就要钱。</span></p><p style=\"text-indent: 37px;\"><span style=\"font-family: 楷体_GB2312; font-size: 14px;\">前期准备的时候，竟然发现我们以前跟交易所和中登的申请过全天候测试环境，可是在公司档案中我竟然一点资料都没有找到。每次升级测试都是草草了事，系统管理员还信誓旦旦的说:“报单没法测试，因为交易所没测试环境，我总不能在生产上报一笔单子吧”。</span></p><p style=\"text-indent: 37px;\"><span style=\"font-family: 楷体_GB2312; font-size: 14px;\">交易所和中登有服务热线，也有交流的群，但这种重复查轮子的事情总是存在。如果我们内部项目实施留存的资料多一点，行业交流活跃些，新项目的实施会轻松很多。</span></p><p style=\"text-indent: 37px;\"><span style=\"font-family: 楷体_GB2312; font-size: 14px;\">在交易所群里提问的时候，大部分是只能依赖交易所的支持人员。除非有特殊交情的同行，一般是没人回答你的问题的。</span></p><p style=\"text-indent: 37px;\"><span style=\"font-family: 楷体_GB2312; font-size: 14px;\">每次培训会议领导都不让参加，结识同行的机会有点少，我们更多的是依靠系统开发商和交易所本身的力量实施项目。这样而言，公司内部的项目实施文档留存记录显得格外重要。</span></p><p style=\"text-indent: 37px;\"><span style=\"font-family: 楷体_GB2312; font-size: 14px;\">鉴于种种原因，自己打算建这么个地方，初步想法就是记录自己工作、项目实施中的点点滴滴。不知道几年后，再次看这些文档记录会是啥想法，希望坦然一笑吧。</span></p><p><br/></p>', '       小编，就职于一家保险公司，基本上就是忙于投资清算、估值核算方面的日常业务操作和系统运维。 为啥想弄个网站出来呢？说是无聊也不过分，主要是系统管理员离职了，兼任其岗位近一年，有点感受越来越深：“公司内部很多项目实施过了，运行上线了，但接手后发现除了系统在正常运行之外，其他什么都没留下”。', '1', null, '99', '5', '0', '1', 'admin', '扶竹', '2016-10-27 20:34:18', '2017-06-28 12:42:56');
INSERT INTO `blog_post` VALUES ('2', '保险公司投资收益营改增问题分析', '<p style=\"text-indent: 37px; text-align: left;\">今年5月份开始，保险公司开始实施营改增，针对该需求，公司采购了软件开发商的估值系统“营改增”增值模块。</p><p style=\"text-align: left;\"><strong>一、目前系统的科目设置</strong></p><p>&nbsp;</p><table width=\"656\"><tbody><tr style=\"height: 19px;\" class=\"firstRow\"><td width=\"109\" style=\"background: rgb(191, 191, 191); border-color: rgb(0, 0, 0); padding: 1px;\"><p style=\"text-align: left; vertical-align: middle;\">科目代码</p></td><td width=\"312\" style=\"background: rgb(191, 191, 191); padding: 1px; border-top-color: rgb(0, 0, 0); border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-style: none;\"><p style=\"text-align: left; vertical-align: middle;\">科目名称</p></td><td width=\"235\" style=\"background: rgb(191, 191, 191); padding: 1px; border-top-color: rgb(0, 0, 0); border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-style: none;\"><p style=\"text-align: left; vertical-align: middle;\">备注</p></td></tr><tr style=\"height: 19px;\"><td width=\"109\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-color: rgb(0, 0, 0); border-top-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">222104</p></td><td width=\"312\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"text-align: left; vertical-align: top;\">应交税费-应交增值税-销项税额</p></td><td width=\"235\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><br/></td></tr><tr style=\"height: 19px;\"><td width=\"109\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-color: rgb(0, 0, 0); border-top-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">222105</p></td><td width=\"312\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"text-align: left; vertical-align: top;\">应交税费-应交增值税-进项税额</p></td><td width=\"235\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><br/></td></tr><tr style=\"height: 19px;\"><td width=\"109\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-color: rgb(0, 0, 0); border-top-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">222106</p></td><td width=\"312\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"text-align: left; vertical-align: top;\">应交税费-应交增值税-金融商品买卖销项税额</p></td><td width=\"235\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"text-align: left; vertical-align: top;\">价差收入自动计提科目</p></td></tr><tr style=\"height: 19px;\"><td width=\"109\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-color: rgb(0, 0, 0); border-top-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">222107</p></td><td width=\"312\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"text-align: left; vertical-align: top;\">应交税费-应交增值税-待结转销项税</p></td><td width=\"235\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><br/></td></tr><tr style=\"height: 19px;\"><td width=\"109\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-color: rgb(0, 0, 0); border-top-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">22210701</p></td><td width=\"312\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"vertical-align: top; text-align: left;\">应交税费-应交增值税-待结转销项税-利息收入</p></td><td width=\"235\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><br/></td></tr><tr style=\"height: 32px;\"><td width=\"109\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-color: rgb(0, 0, 0); border-top-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">2221070101</p></td><td width=\"312\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"vertical-align: top; text-align: left;\">应交税费-应交增值税-待结转销项税-利息收入-债券_市场</p></td><td width=\"235\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"vertical-align: bottom; text-align: left;\">债券利息收入自动计提科目，属过渡科目；按市场类别明细核算</p></td></tr><tr style=\"height: 32px;\"><td width=\"109\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-color: rgb(0, 0, 0); border-top-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">2221070101001</p></td><td width=\"312\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"text-align: left; vertical-align: top;\">应交税费-应交增值税-待结转销项税-利息收入-债券_市场_券</p></td><td width=\"235\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"vertical-align: bottom; text-align: left;\">债券利息收入自动计提科目，属过渡科目；按证券代码明细核算</p></td></tr></tbody></table><p>&nbsp;</p><p style=\"text-indent: 37px; text-align: left;\">通过比较“应交增值税-金融商品买卖销项税额”是在发生价差收入的时候自动计提的，且是汇总级别的，并没有明细到具体的证券；而对于债券类的利息收入则是具体到券的。</p><p style=\"text-indent: 37px; text-align: left;\">科目设置的不同，一方面是由于系统实现限制，另一方面就是业务本身要求的。</p><p style=\"text-align: left;\"><strong>二、债券类利息收入应交增值税科目设置分析</strong></p><p style=\"text-indent: 37px; text-align: left;\">若不明晰，在债券计提利息收入的时候，全部计提到“应交税费-应交增值税-待结转销项税-利息收入”，不区分到具体的明细科目。</p><p style=\"text-indent: 37px; text-align: left;\">“应交税费-应交增值税-待结转销项税-利息收入”这部分在什么时候缴纳，是否和具体的债券有关系呢，该科目余额不可能为负数，如果月底余额是多少我就缴纳多少，这种方式是和具体的券没有关系的，当然该科目不需要明细。</p><p style=\"text-indent: 37px; text-align: left;\">但现实中，在企业还没收到债券的实际收益的时候就缴纳增值税，显然是不现实的。大多数是倾向于在兑息的时候缴纳增值税，或者卖出、兑付的时候缴纳增值税，这种缴纳方式就和具体的券有关系啦。</p><p style=\"text-indent: 37px; text-align: left;\">所以根据实际业务要求，“应交税费-应交增值税-待结转销项税-利息收入”该类科目是需要明细到具体的证券的。</p><p style=\"text-align: left;\">三、<strong>应交增值税-金融商品买卖销项税额</strong></p><p style=\"text-indent: 37px; text-align: left;\">对于“应交税费-应交增值税-金融商品买卖销项税额”这部分的缴纳现在比较认同，如果当月为正数，则缴纳的模式。</p><p style=\"text-indent: 37px; text-align: left;\">系统基本上也是按照这个逻辑实现的：</p><p style=\"text-indent: 37px; text-align: left;\">1.“222106应交增值税-金融商品买卖销项税额”每个月末会结转到“222104应交增值税-销项税额”</p><p style=\"text-indent: 37px; text-align: left;\">2.如果“222106应交增值税-金融商品买卖销项税额”科目余额为正，则结转科目余额；如果科目余额为负，则不结转。</p><p style=\"text-indent: 37px; text-align: left;\">在此提出系统的一个未考虑周全的地方，没有想到年末如果为负数，但应缴税额又不能延续到下一年度的情况。</p><p style=\"text-indent: 37px; text-align: left;\">这么看下来，“应交税费-应交增值税-金融商品买卖销项税额”科目是否明细到证券级别没有什么影响，无论是明细与否，我都是按照222106科目余额是否为正判断的，无非就是结转的时候，由一个父类科目变成了多个底级明细科目、判断科目余额变成了判断多个底级科目的余额汇总值是否为正。</p><p style=\"text-indent: 37px; text-align: left;\">下面我们根据假设的业务情况，进行一定的延伸分析，然后再总结个答案。</p><p style=\"text-align: left;\"><strong>四、场景假设</strong></p><p style=\"text-indent: 37px; text-align: left;\">假定某组合只有股票构成，股票买卖差价收入的应缴纳增值税率为6%，年初只有A、B、C、D四只股票，成本和数量具体如下表所示。</p><table width=\"216\"><tbody><tr style=\"height: 19px;\" class=\"firstRow\"><td width=\"72\" style=\"border-color: rgb(0, 0, 0); padding: 1px;\"><br/></td><td width=\"72\" style=\"padding: 1px; border-top-color: rgb(0, 0, 0); border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-style: none;\"><p style=\"text-align: left; vertical-align: middle;\">成本</p></td><td width=\"72\" style=\"padding: 1px; border-top-color: rgb(0, 0, 0); border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-style: none;\"><p style=\"text-align: left; vertical-align: middle;\">数量</p></td></tr><tr style=\"height: 19px;\"><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-color: rgb(0, 0, 0); border-top-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">股票A</p></td><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">5,000</p></td><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">500</p></td></tr><tr style=\"height: 19px;\"><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-color: rgb(0, 0, 0); border-top-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">股票B</p></td><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">20,000</p></td><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">2,000</p></td></tr><tr style=\"height: 19px;\"><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-color: rgb(0, 0, 0); border-top-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">股票C</p></td><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">50,000</p></td><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">5,000</p></td></tr><tr style=\"height: 19px;\"><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-color: rgb(0, 0, 0); border-top-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">股票D</p></td><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">5,000</p></td><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">500</p></td></tr></tbody></table><p style=\"text-indent: 37px; text-align: left;\">1.为了简化推理过程的逻辑，我们只做卖出股票的动作，一份月卖出情况如下表所示。</p><table width=\"452\"><tbody><tr style=\"height: 19px;\" class=\"firstRow\"><td width=\"72\" style=\"border-color: rgb(0, 0, 0); padding: 1px;\"><br/></td><td width=\"72\" style=\"padding: 1px; border-top-color: rgb(0, 0, 0); border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">卖出数量</p></td><td width=\"72\" style=\"padding: 1px; border-top-color: rgb(0, 0, 0); border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">剩余数量</p></td><td width=\"132\" style=\"padding: 1px; border-top-color: rgb(0, 0, 0); border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">发生买卖差价收入</p></td><td width=\"104\" style=\"padding: 1px; border-top-color: rgb(0, 0, 0); border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">计提增值税</p></td></tr><tr style=\"height: 19px;\"><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-color: rgb(0, 0, 0); border-top-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">股票A</p></td><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><br/></td><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">500</p></td><td width=\"132\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><br/></td><td width=\"104\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><br/></td></tr><tr style=\"height: 19px;\"><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-color: rgb(0, 0, 0); border-top-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">股票B</p></td><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">200</p></td><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">1,800</p></td><td width=\"132\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">-400</p></td><td width=\"104\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">-24</p></td></tr><tr style=\"height: 19px;\"><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-color: rgb(0, 0, 0); border-top-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">股票C</p></td><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">1,000</p></td><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">4,000</p></td><td width=\"132\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">10,000</p></td><td width=\"104\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">600</p></td></tr><tr style=\"height: 19px;\"><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-color: rgb(0, 0, 0); border-top-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">股票D</p></td><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">500</p></td><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">0</p></td><td width=\"132\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">50</p></td><td width=\"104\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">3</p></td></tr></tbody></table><p style=\"text-indent: 37px; text-align: left;\">“应交增值税-金融商品买卖销项税额”科目余额月末为579，正数，将结转到“应交增值税-销项税额”，代表本月缴纳579。</p><p style=\"text-align: left;\">&nbsp;&nbsp;&nbsp;&nbsp;假设该科目明细到具体的证券，则结转后各科目余额如下表所示：</p><table width=\"513\"><tbody><tr style=\"height: 19px;\" class=\"firstRow\"><td width=\"72\" style=\"border-color: rgb(0, 0, 0); padding: 1px;\"><br/></td><td width=\"369\" style=\"padding: 1px; border-top-color: rgb(0, 0, 0); border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-style: none;\"><p style=\"text-align: left; vertical-align: middle;\">科目</p></td><td width=\"72\" style=\"padding: 1px; border-top-color: rgb(0, 0, 0); border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-style: none;\"><p style=\"text-align: left; vertical-align: middle;\">余额</p></td></tr><tr style=\"height: 19px;\"><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-color: rgb(0, 0, 0); border-top-style: none;\" rowspan=\"4\"><p style=\"text-align: left; vertical-align: middle;\">底级科目</p></td><td width=\"369\" style=\"padding: 1px; border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">应缴增值税-金融商品买卖销项税额-股票A</p></td><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-left-color: rgb(0, 0, 0); border-top-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">0</p></td></tr><tr style=\"height: 19px;\"><td width=\"369\" style=\"padding: 1px; border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">应缴增值税-金融商品买卖销项税额-股票B</p></td><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-left-color: rgb(0, 0, 0);\"><p style=\"vertical-align: middle; text-align: left;\">0</p></td></tr><tr style=\"height: 19px;\"><td width=\"369\" style=\"padding: 1px; border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">应缴增值税-金融商品买卖销项税额-股票C</p></td><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-left-color: rgb(0, 0, 0);\"><p style=\"vertical-align: middle; text-align: left;\">0</p></td></tr><tr style=\"height: 19px;\"><td width=\"369\" style=\"padding: 1px; border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">应缴增值税-金融商品买卖销项税额-股票D</p></td><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-left-color: rgb(0, 0, 0);\"><p style=\"vertical-align: middle; text-align: left;\">0</p></td></tr><tr style=\"height: 19px;\"><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-color: rgb(0, 0, 0); border-top-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">父类科目</p></td><td width=\"369\" style=\"padding: 1px; border-top-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">应缴增值税-金融商品买卖销项税额</p></td><td width=\"72\" style=\"border-color: rgb(0, 0, 0); padding: 1px;\"><p style=\"vertical-align: middle; text-align: left;\">0</p></td></tr></tbody></table><p style=\"text-indent: 37px; text-align: left;\">2.二份月卖出情况如下表所示，为了说明后续问题，我们让股票A和一月份的D一样，卖空。</p><table width=\"452\"><tbody><tr style=\"height: 19px;\" class=\"firstRow\"><td width=\"72\" style=\"border-color: rgb(0, 0, 0); padding: 1px;\"><br/></td><td width=\"72\" style=\"padding: 1px; border-top-color: rgb(0, 0, 0); border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-style: none;\"><p style=\"text-align: left; vertical-align: middle;\">卖出数量</p></td><td width=\"72\" style=\"padding: 1px; border-top-color: rgb(0, 0, 0); border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-style: none;\"><p style=\"text-align: left; vertical-align: middle;\">剩余数量</p></td><td width=\"132\" style=\"padding: 1px; border-top-color: rgb(0, 0, 0); border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-style: none;\"><p style=\"text-align: left; vertical-align: middle;\">收益</p></td><td width=\"104\" style=\"padding: 1px; border-top-color: rgb(0, 0, 0); border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-style: none;\"><p style=\"text-align: left; vertical-align: middle;\">增值税</p></td></tr><tr style=\"height: 19px;\"><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-left-color: rgb(0, 0, 0); border-top-style: none;\"><p style=\"vertical-align: middle; text-align: left;\">股票A</p></td><td width=\"72\" style=\"padding: 1px; border-right-color: rgb(0, 0, 0); border-bottom-color: rgb(0, 0, 0); border-top-style: none; border-left-style: none;\"><p style=\"vertical-align: middle; text-align: left;